/**
 * @param {Processor} processor
 * @returns {MinimalDuplex}
 */
export function stream(processor: Processor): MinimalDuplex
export type Processor = import('unified').Processor
export type ProcessCallback = import('unified').ProcessCallback
export type Encoding = import('vfile').BufferEncoding
export type Value = import('vfile').VFileValue
export type Callback = (error?: Error | undefined) => void
export type MinimalDuplex = Omit<
  NodeJS.ReadableStream & NodeJS.WritableStream,
  | 'read'
  | 'setEncoding'
  | 'pause'
  | 'resume'
  | 'isPaused'
  | 'unpipe'
  | 'unshift'
  | 'wrap'
>
